package loop;

public class quiz10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// 중첩반복문을 사용해서 직각삼각형을 그리기
	
		
			for (int i = 1; i <= 5; i++) {
				for(int j = 1; j<=i; j++) {
					System.out.print("*");					
				}
				System.out.println("");
			}
		
	
}
	
}
